import React, { useContext, useState, useEffect } from 'react';
import { SettingsContext } from '../../context/settings';

import { Button, Card, Elevation } from '@blueprintjs/core'

const List = ({ list, toggleComplete }) => {

  const settings = useContext(SettingsContext);

  const [page, setPage] = useState(1);

  const renderItems = () => {
    let items = [];
    let start = (settings.itemsToDisplay * page) - 3;
    let end = (settings.itemsToDisplay * page);
    if (list.length > settings.itemsToDisplay) {
      for (let i = start; i < end; i++) {
        items.push(list[i]);
      }
      return items;
    } else {
      return list;
    }
  }

  return (
    <>
      {renderItems().map(item => (  
        item ?
        <Card key={item.id} elevation={Elevation.TWO}>
          <h3>{item.text}</h3>
          <p>Assigned to: {item.assignee}</p>
          <p>Difficulty: {item.difficulty}</p>
          <Button onClick={() => toggleComplete(item.id)} className={item.complete ? 'bp4-intent-warning' : 'bp4-intent-success'}>Complete: {item.complete.toString()}</Button>
          <hr />
        </Card> : null
      ))
      }
      {list.length > (settings.itemsToDisplay * 2) || page > 1 ? 
      <Button type='click' onClick={() => setPage(page - 1)}>Previous</Button> : null}
      {list.length > settings.itemsToDisplay ? 
      <Button type='click' onClick={() => setPage(page + 1)}>Next</Button> : null}
    </>
  )
}

export default List;
