import React from 'react'
import { FormGroup, Label, Switch, NumericInput } from '@blueprintjs/core';
import { SettingsContext } from '../../context/settings'
import { useContext, useState } from 'react';

const Settings = () => {

  const settings = useContext(SettingsContext);
  const [checked, setChecked] = useState(false);

  return (
    <FormGroup>
      <h2>Settings</h2>
      <Label>
        Show Completed
        <Switch checked={checked} onChange={() => setChecked(!checked)}/>
      </Label>
        Items To Display
        <NumericInput 
          max={5}
          min={1}
          placeholder='Items to display per page'
          onButtonClick={settings.itemsToDisplay}

        />
      <Label>

      </Label>
    </FormGroup>
  )
}

export default Settings;
