import React, { useContext, useState, useEffect } from 'react';
import { SettingsContext } from '../../context/settings';

const List = ({ list, toggleComplete }) => {

  const settings = useContext(SettingsContext);

  const [page, setPage] = useState(1);

  const renderItems = () => {
    let items = [];
    let start = (settings.itemsToDisplay * page) - 3;
    let end = (settings.itemsToDisplay * page);
    if (list.length > settings.itemsToDisplay) {
      for (let i = start; i < end; i++) {
        items.push(list[i]);
      }
      return items;
    } else {
      return list;
    }
  }

  return (
    <>
      {renderItems().map(item => (

        <div key={item.id}>
          <p>{item.text}</p>
          <p><small>Assigned to: {item.assignee}</small></p>
          <p><small>Difficulty: {item.difficulty}</small></p>
          <div onClick={() => toggleComplete(item.id)}>Complete: {item.complete.toString()}</div>
          <hr />
        </div>
      ))
      }
      {list.length > (settings.itemsToDisplay * 2) ? 
      <button>Previous</button> : null}
      {list.length > settings.itemsToDisplay ? 
      <button>Next</button> : null}
    </>
  )
}

export default List;
