import React, { useContext, useState, useEffect } from 'react';
import { render } from 'react-dom/cjs/react-dom.production.min';
import { SettingsContext } from '../../context/settings';

const List = ({ list, toggleComplete }) => {

  const settings = useContext(SettingsContext);

  const [page, setPage] = useState(1);

  const renderResults = () => {
    let listItems = [];
    let start = (settings.itemsToDisplay * page) - 3;
    let end = (settings.itemsToDisplay * page) - 1;
    for (let i = start; i < end; i++) {
      listItems.push(<div key={list[i].id}>
          <p>{list[i].text}</p>
          <p><small>Assigned to: {list[i].assignee}</small></p>
          <p><small>Difficulty: {list[i].difficulty}</small></p>
          <div onClick={() => toggleComplete(list[i].id)}>Complete: {list[i].complete.toString()}</div>
          <hr />
        </div>)
    }
    return listItems;
  }


  return (
    <>
      {renderResults().map(item => (

        <div key={item.id}>
          <p>{item.text}</p>
          <p><small>Assigned to: {item.assignee}</small></p>
          <p><small>Difficulty: {item.difficulty}</small></p>
          <div onClick={() => toggleComplete(item.id)}>Complete: {item.complete.toString()}</div>
          <hr />
          {/* <button type='click' onClick={displayList}>Next</button> */}
        </div>

      ))
      }
    </>
  )
}

export default List;
